import { Button } from '@zeit-ui/react';
import { Archive } from '@zeit-ui/react-icons';
import { makeStyles } from '@material-ui/core/styles';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import React from 'react';
import Collapse from '@material-ui/core/Collapse';
import Link from '../../src/Link';

const useStyles = makeStyles((theme) => ({
    margin: {
      margin: theme.spacing(1),
    },
    extendedIcon: {
      marginRight: theme.spacing(1),
    },
  }));



const MinimalButton = () => {

    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
      setAnchorEl(null);
    };


    return (
        <React.Fragment>
        <Button icon={<Archive />} effect='false' aria-label="add" type="success-light" aria-haspopup="false" onClick={handleClick}>
        Facilities</Button> 

         <Menu
         id="simple-menu"
         anchorEl={anchorEl}
         open={Boolean(anchorEl)}
         onClose={handleClose}
         TransitionComponent={Collapse}
       >
         <MenuItem component={Link} naked href="/testing">Flow Chart</MenuItem>
         <MenuItem>Testing Facilities</MenuItem>
         <MenuItem>Quality Promise</MenuItem>
       </Menu>
       </React.Fragment>
 )
} 
export default MinimalButton;
