const [isShown, setIsShown] = useState(false);

const DropDown = () => (
    <div>
    <div className='inline mx-4 hover:text-indigo-600'>Facilities</div>
    {isShown && (
        <div>
          I'll appear when you hover over the button.
        </div>
      )}

      </div>            
);

export default DropDown;
