import React, { Component } from 'react';
import PORTFOLIO_DATA from '../Data/Data.js';
import ProjectCards from '../Cards/Projects';
import { PreviewContainer, MobileOnly } from '../portfolio.styles';
import BusinessIcon from '@material-ui/icons/Business';
import Blob from '../images/decorations/blob1';
import Fade from 'react-reveal/Fade';
import ProImage from '../images/folios/projectimage';

import { Paper } from '@material-ui/core';

import SwipeableViews from 'react-swipeable-views';


class Portfolio extends Component {
constructor(props){
    super(props);

    this.state = {
       projects : PORTFOLIO_DATA

    }
}

    render() {
        const {projects} = this.state;
        const styles = {
            root: {
              padding: '0 2rem',
              marginLeft : '-0.9rem'
            },
            slideContainer: {
              marginLeft:'-0.2rem',

            }, 

          };

        return (
     <div className='m-12 md:ml-16'>

     <div className='mb-4 md:mb-4 text-indigo-700 text-bold text-center text-4xl md:text-6xl font-medium'> 
     <BusinessIcon style={{ fontSize: 40 }}/> Our Clients </div>
      <MobileOnly>
      <SwipeableViews style={styles.root} slideStyle={styles.slideContainer}>
             {projects.map(({id, title, description, imageUrl})=> (
                      <ProjectCards key={id} title={title} description={description} imageUrl={imageUrl}/>
                  ))}
              </SwipeableViews>
      </MobileOnly>

            <PreviewContainer>
                {projects.map(({id, title, description,imageUrl})=> (
                          <ProjectCards key={id} title={title} description={description} imageUrl={imageUrl}/> ))}
            </PreviewContainer>      
    </div>
        )
    }
}


export default Portfolio;
