import React, { Component } from 'react';

import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SideDrawer from './SideDrawer';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import { PreviewContainer, MobileOnly } from '../portfolio.styles';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';




class Header extends Component {

       state = { 
           drawerOpen: false,
           headerShow: false
             }

        componentDidMount() {
               window.addEventListener('scroll',this.handleScroll); 
             }

    handleScroll = () => {

if(window.scrollY > 0){
    this.setState ({
        headerShow:true
    })
}
 else {
    this.setState ({
        headerShow:false
    })

 }
    }

    toggleDrawer = (value) => {
        this.setState({
    drawerOpen: value
        })
    }


    render() {
        const useStyles = makeStyles(theme => ({

            toolbarMargin : {
              ...theme.mixins.toolbar,
              marginBottom: "3em",
            },

            logo : {
              height: "7rem"
            },

            tabsContainer : {
              marginLeft: "auto",
            },

            tabs : {
             ...theme.typography.tabs,
              minWidth: "10",
              marginLeft: "20px"
            }
          }))

        return (
            <div className='mb-16'>
            <AppBar 
            position="fixed"
            style={{
                backgroundColor: this.state.headerShow ? '#F7FAFC' : 'white' ,
                boxShadow:this.state.headerShow ? '0px 2px 4px -1px rgba(0,0,0,0.2),0px 4px 5px 0px rgba(0,0,0,0.14),0px 1px 10px 0px rgba(0,0,0,0.12)' : 'none' ,
                padding:'10px 0px',
             }}>
        <Toolbar>
            <div style={{flexGrow:1}}>
               <div className='text-indigo-400 text-xl' > RK Engineering </div>
               <div className='text-indigo-300' > Corporation </div>
            </div>
            <MobileOnly>
               <IconButton color='primary' aria-label='Menu' onClick={()=> this.toggleDrawer(true)}> 
                 <MenuIcon/>
             </IconButton>
              <SideDrawer
                   open={this.state.drawerOpen}
                  onClose={(value)=> this.toggleDrawer(value)}/>
                  </MobileOnly>

                  <PreviewContainer>
                  <Tabs className={classes.tabsContainer}>
                    <Tab label="Home" className={classes.tabs}/>
                    <Tab label="Services" className={classes.tabs}/>
                    <Tab label="The Revolution" className={classes.tabs}/>
                    <Tab label="About Us" className={classes.tabs}/>
                    <Tab label="Contact Us" className={classes.tabs}/>
                 </Tabs>
                  </PreviewContainer>
       </Toolbar>
            </AppBar>   
            </div>
        );
    }
}

export default Header;
